function displayContactEmailAddress() {
	//alert('Inside action');

	//var email = g_form.getValue('u_requested_for_email');
	//jslog("HOWIE: The value of email = " + email);
	var contact_id = g_form.getValue('u_requested_for');
	jslog("HOWIE: The value of contact_id = " + contact_id);
	//alert('Requested For email address is: ' + email);
	//alert('Requested For(sys_id) is: ' + contact_id);
	// Pass the Requested for email to the Script Include.  Store the return
	// value from the Script Include in the isEmail variable
	//var isEmail = validateEmailAddress(email);
	try {
		//var emailAddress = new x_58872_needit.GetEmailAddress();
		//jslog("HOWIE: The value of emailAddress = " + emailAddress);
		//var contact_email = new x_58872_needit.GetEmailAddress().getEmail(contact_id);
		//jslog("HOWIE: The value of contact_email = " + contact_email);
		// Instantiate the GetEmailAddress Script Include
		var getEmailAddr = new GlideAjax('GetEmailAddress');
		jslog("HOWIE: The value of getEmailAddr = " + getEmailAddr);
		// Specify the getEmail method
		getEmailAddr.addParam('sysparm_name','getEmail');
		// Pass the Requested for sys_id
		getEmailAddr.addParam('sysparm_userID', contact_id);
		// Send the request to the server
		getEmailAddr.getXML(populateEmailField);



	}
	catch(err) {
		jslog('A JavaScript runtime error occurred: ' + err.message);
	}
	// If isEmail is false (email address syntax is not valid) do not save
	// the record.  Write an error message to the screen.
	//if (isEmail == true) {
		//	gs.addInfoMessage(email + " is a valid email address.");
		//} else {
			//    gs.addErrorMessage(email + " is not a valid email address.  You must provide a valid email address.");
			//}
	// When the response is back from the server
		function populateEmailField(response){
			jslog("HOWIE: The value of response = " + response);
			// Extract the email address from the response, clear any value from the email field,
			// set new value in the email field
			var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
			jslog("HOWIE: The value of emailFromScriptInclude = " + emailFromScriptInclude);
			//g_form.clearValue('u_requested_for_email');
			//g_form.setValue('u_requested_for_email',emailFromScriptInclude);
			var message = "The contact email address is: " + emailFromScriptInclude;
			//gs.addInfoMessage("The contact email address is: " + emailFromScriptInclude);
			alert(message);
		}
}
