replaceAllEmailAddresses();

function replaceAllEmailAddresses()
{
	//var niutils = new NeedItUtils();
	//niutils.replaceAllEmailAddresses();
	var niutils = new NeedItUtils();
	var oldValue =  'example.no';
	var newValue = 'example.com';

	var bookingRecords = new GlideRecord('x_58872_needit_needit');
	bookingRecords.query();
	while (bookingRecords.next()) {
		email = bookingRecords.u_requested_for_email;
		if ((email == '') || (email.indexOf(oldValue) == -1))
			continue;

		var newEmail = niutils.replaceEmailAddress(email,oldValue,newValue);
		gs.info('Old email address:' + email + ',New email address:' + newEmail, 'replaceAllEmailAddresses');

		//bookingRecords.u_requested_for_email = newEmail;
		//bookingRecords.update();
	}

}
